import './GraphComponentStyle.css';
import React from 'react';
import {getBubbleSort} from '../bubble/Bubble'
let runningBubble1;
let runningBubble2;
let stop = true;
class GraphComponent extends React.Component{
    constructor(){
        super();
        this.state = {
            numbers: [],
            numOfBars: 5,
        }
    }
    changeColor(){
        console.log(document.getElementsByClassName('bar'));
        console.log(bars);
        // let idx = 0;
        // let len = bars.length;
        // let time = setInterval(()=>{
        //     bars[idx].style.backgroundColor = 'black';
        //     if(idx>=len){
        //         for(let it = 0; it < len; it++)
        //             bars[it].style.backgroundColor = 'red';
        //         clearInterval(time);
        //     }
        //     idx++;
        //     console.log(idx);            
        // },1000)
    }
    bubbleSort(){
        stop = false;
        let [animations] = (stop) ? null : getBubbleSort(this.state.numbers);
        for(let i = 0; i < animations.length&&!stop; i++){
            const isColorChange = ((i%4===0)||(i%4===1));
            const arrayBars = document.getElementsByClassName('bar');
            if(isColorChange){
                const color = (i%4===0) ? 'black' : 'red';
                const [barOneIdx, barTwoIdx] = animations[i];
                const barOneStyle = arrayBars[barOneIdx].style;
                const barTwoStyle = arrayBars[barTwoIdx].style;
                runningBubble1 = setTimeout(() => {
                    barOneStyle.backgroundColor = color;
                    barTwoStyle.backgroundColor = color;
                }, i*1);
            }else{
                const [barIdx, newHeight] = animations[i];
                if(barIdx===-1){
                    continue;
                }
                const barStyle = arrayBars[barIdx].style;
                runningBubble2 = setTimeout(() => {
                    barStyle.height = `${newHeight}%`;
                }, i*1);
            }
        }
    }
    initializeNumbers(){
        stop=true;
        window.clearTimeout(runningBubble1);
        window.clearTimeout(runningBubble2);
        runningBubble1=null;
        runningBubble2=null;
        console.log(runningBubble1);
        console.log(runningBubble1);
        let nums = [];
        for(let i = 0; i < this.state.numOfBars; i++){
            nums.push(Math.floor(Math.abs(Math.random()-Math.random())*(100))+1)
        }
        this.setState({
            numbers: nums,
        });
    }
    componentDidMount(){
        this.initializeNumbers();
    }
    render(){
        console.log(runningBubble1);
        console.log(runningBubble2);
        return (
            <div className='container'>
                <div className="bars">
                    {this.state.numbers.map((value, index) => (<div className="bar" style={{height:((value))+'%', width:(100/this.props.numOfBars)+'%', background: 'red'}} key={index}></div>))}
                </div>
                <div className="input-numbars">
                    <input className="input" type='range' min='5' max='300' value={this.state.numOfBars} onChange={(e) => {this.setState({numOfBars:e.target.value});}}></input>
                    <p className="input-text">Number of elements: {this.state.numOfBars}</p>
                </div>
                <div className="button-area">
                    <button onClick={()=>this.initializeNumbers()}className="button-newRandom">Reset Array</button>
                    <button onClick={()=>this.changeColor()}className="button-sort">Bubble Sort</button>
                </div>
            </div>
        );
    }
}
export default GraphComponent;
