import './GraphComponentStyle.css';
import React from 'react';
import {getBubbleSort} from '../bubble/Bubble'
class GraphComponent extends React.Component{
    constructor(){
        super();
        this.state = {
            numbers: [],
            numOfBars: 5,
        }
    }
    bubbleSort(){
        let [animations] = getBubbleSort(this.state.numbers);
        for(let i = 0; i < animations.length; i++){
            const isColorChange = ((i%4===0)||(i%4===1));
            const arrayBars = document.getElementsByClassName('bars');
            if(isColorChange){
                const color = (i%4===0) ? 'red' : 'turquoise';
                const [barOneIdx, barTwoIdx] = animations[i];
                const barOneStyle = arrayBars[barOneIdx].style;
                const barTwoStyle = arrayBars[barTwoIdx].style;
                setTimeout(() => {
                    barOneStyle.backgroundColor = color;
                    barTwoStyle.backgroundColor = color;
                }, i*1);
            }else{
                const [barIdx, newHeight] = animations[i];
                if(barIdx===-1){
                    continue;
                }
                const barStyle = arrayBars[barIdx].style;
                setTimeout(() => {
                    barStyle.height = `${newHeight}px`;
                }, i*1);
            }
        }
    }
    initializeNumbers(){
        let nums = [];
        for(let i = 0; i < this.state.numOfBars; i++){
            nums.push(Math.floor(Math.abs(Math.random()-Math.random())*(100))+1)
        }
        this.setState({
            numbers: nums,
        });
    }
    componentDidMount(){
        this.initializeNumbers();
    }
    render(){
        return (
            <div className='container'>
                <div className="bars">
                    {this.state.numbers.map((value, index) => (<div className="bar" style={{height:((value))+'%', width:(100/this.props.numOfBars)+'%', background: 'red'}} key={index}></div>))}
                </div>
                <div className="input-numbars">
                    <input className="input" type='range' min='5' max='250' value={this.state.numOfBars} onChange={(e) => {this.setState({numOfBars:e.target.value});}}></input>
                    <p className="input-text">Number of elements: {this.state.numOfBars}</p>
                </div>
                <div className="button-area">
                    <button onClick={()=>this.initializeNumbers()}className="button-newRandom">Reset Array</button>
                    <button onClick={()=>this.bubbleSort()}className="button-sort">Bubble Sort</button>
                </div>
            </div>
        );
    }
}
export default GraphComponent;
