import './GraphComponentStyle.css';
import React from 'react';

class GraphComponent extends React.Component{
    constructor(){
        super();
        this.state = {
            numbers: [],
            numOfBars: 5,
        }
    }
    bubbleSort(){
        let auxNumber = this.state.numbers;
        for(let i = 0; i < this.state.numbers.length; i++){
            for(let j = i+1; j < this.state.numbers.length; j++){
                if(auxNumber[i] > auxNumber[j]){
                    let aux = auxNumber[i];
                    auxNumber[i] = auxNumber[j];
                    auxNumber[j]=aux;
                }
            }
        }
        this.setState({
            auxNumber
        });
    }
    initializeNumbers(){
        let nums = [];
        for(let i = 0; i < this.state.numOfBars; i++){
            nums.push(Math.floor(Math.abs(Math.random()-Math.random())*(100))+1)
        }
        this.setState({
            numbers: nums,
        });
    }
    componentDidMount(){
        this.initializeNumbers();
    }
    render(){
        return (
            <div className='container'>
                <div className="bars">
                    {this.state.numbers.map((value, index) => (<div className="bar" style={{height:((value))+'%', width:(100/this.props.numOfBars)+'%', background: 'red'}} key={index}></div>))}
                </div>
                <div className="input-numbars">
                    <input className="input" type='range' min='5' max='500' value={this.state.numOfBars} onChange={(e) => {this.setState({numOfBars:e.target.value});}}></input>
                    <p className="input-text">Number of elements: {this.state.numOfBars}</p>
                </div>
                <div className="button-area">
                    <button onClick={()=>this.initializeNumbers()}className="button-sort">Reset Array</button>
                    <button onClick={()=>this.bubbleSort()}className="button-sort">Bubble Sort</button>
                </div>
            </div>
        );
    }
}
export default GraphComponent;
