import './GraphComponentStyle.css';
import React from 'react';
class GraphComponent extends React.Component{
    constructor(){
        super();
        this.state = {
            numbers: [],
            numOfBars: 50,
        }
    }
    initializeNumbers(){
        let nums = [];
        for(let i = 0; i < this.state.numOfBars; i++){
            nums.push(Math.floor(Math.random()*(100))+1)
        }
        this.setState({
            numbers: nums,
        });
    }
    componentDidMount(){
        this.initializeNumbers();
    }
    render(){
        return (
            <div className="bars">
                {this.state.numbers.map((value, index) => (<div className="bar" style={{height:((value))+'%', width:(100/this.state.numOfBars)+'%'}} key={index}></div>))}
            </div>
        );
    }
}
export default GraphComponent;
