import './GraphComponentStyle.css';
import React from 'react';
class GraphComponent extends React.Component{
    constructor(){
        super();
        this.state = {
            numbers: [],
            numOfBars: 100,
        }
    }
    initializeNumbers(){
        let nums = [];
        for(let i = 0; i < this.state.numOfBars; i++){
            nums.push(Math.floor(Math.random()*(100))+1)
        }
        this.setState({
            numbers: nums,
        });
    }
    componentDidMount(){
        this.initializeNumbers();
    }
    render(){
        return (
            <div className='container'>
                <div className="bars">
                    {this.state.numbers.map((value, index) => (<div className="bar" style={{height:((value*100))+'px', width:20+'px', background: 'red'}} key={index}>{value}</div>))}
                </div>
                <button className="button-newRandom">New Random Array</button>
            </div>
        );
    }
}
export default GraphComponent;
