import './GraphComponentStyle.css';
import React from 'react';
import {getBubbleSort} from '../bubble/Bubble';
let stop = true;
class GraphComponent extends React.Component{
    constructor(){
        super();
        this.state = {
            colorInitial: 'rgba(255, 0, 13, 0.8)',
            colorSelectionSort: 'rgba(45, 45, 45, 0.98)',
            colorSorted: 'rgba(8, 156, 249, 0.8)',
            numbers: [],
            numOfBars: 5,
        }
    }
    selectionSort(){
        let i = 0;
        let j = 0;
        let maxV = this.state.numbers[0];
        let idxMax = 0;
        const bars = document.getElementsByClassName('bar');
        let n = this.state.numbers.length;
        let timer = setInterval(() => {
            if(i<n){
                j = 0;
                while(j<n){
                    if(j>0){
                        bars[j-1].style.backgroundColor = this.state.colorInitial;
                    }
                    if(this.state.numbers[j]>maxV){
                        bars[idxMax].style.backgroundColor = this.state.colorInitial;
                        idxMax = j;
                        maxV = this.state.numbers[j];
                        bars[idxMax].style.backgroundColor = this.state.colorSorted;
                    }else{
                        bars[j].style.backgroundColor = this.state.colorSelectionSort;
                    }
                    j++
                }
            }else{
                clearInterval(timer);
            }
            i++;
            console.log(i);
        }, 1000);
    }
    bubbleSort(){
        stop = false;
        let [animations] = (stop) ? null : getBubbleSort(this.state.numbers);
        for(let i = 0; i < animations.length&&!stop; i++){
            const isColorChange = ((i%4===0)||(i%4===1));
            const arrayBars = document.getElementsByClassName('bar');
            if(isColorChange){
                const color = (i%4===0) ? 'black' : 'rgba(8, 156, 249, 0.8)';
                const [barOneIdx, barTwoIdx] = animations[i];
                const barOneStyle = arrayBars[barOneIdx].style;
                const barTwoStyle = arrayBars[barTwoIdx].style;
                setTimeout(() => {
                    barOneStyle.backgroundColor = color;
                    barTwoStyle.backgroundColor = color;
                }, i*1);
            }else{
                const [barIdx, newHeight] = animations[i];
                if(barIdx===-1){
                    continue;
                }
                const barStyle = arrayBars[barIdx].style;
                setTimeout(() => {
                    barStyle.height = `${newHeight}%`;
                }, i*1);
            }
        }
    }
    initializeNumbers(){
        stop=true;
        let nums = [];
        for(let i = 0; i < this.state.numOfBars; i++){
            nums.push(Math.floor(Math.abs(Math.random()-Math.random())*(100))+1)
        }
        this.setState({
            numbers: nums,
        });
    }
    componentDidMount(){
        this.initializeNumbers();
    }
    render(){
        return (
            <div className='container'>
                <div className="bars">
                    {this.state.numbers.map((value, index) => (<div className="bar" style={{height:((value))+'%', width:(100/this.props.numOfBars)+'%', background: this.state.colorInitial}} key={index}></div>))}
                </div>
                <div className="input-numbars">
                    <input className="input" type='range' min='5' max='300' value={this.state.numOfBars} onChange={(e) => {this.setState({numOfBars:e.target.value});}}></input>
                    <p className="input-text">Number of elements: {this.state.numOfBars}</p>
                </div>
                <div className="button-area">
                    <button onClick={()=>this.initializeNumbers()}className="button-newRandom">Reset Array</button>
                    <button onClick={()=>this.selectionSort()}className="button-sort">Bubble Sort</button>
                </div>
            </div>
        );
    }
}
export default GraphComponent;
