export function getBubbleSort(array){
    let animations = [];
    let auxArray = array.slice();
    bubbleSort(auxArray, animations);
    array = auxArray;
    return [animations, auxArray];
}

function bubbleSort(auxArray, animations){
    const length = auxArray.length;
    for(let i = 0; i < length-1; i++){
        for(let j = 0; j < length-i-1; j++){
            animations.push([j,j+1]);
            animations.push([j,j+1]);
            if(auxArray[j] > auxArray[j+1]){
                animations.push([j, auxArray[j+1]]);
                animations.push([j+1, auxArray[j]]);
                swap(auxArray, j, j+1);
            }else{
                animations.push([-1,-1]);
                animations.push([-1,-1]);
            }
        }
    }
}

function swap(auxArray, idx1, idx2){
    let temp = auxArray[idx1];
    auxArray[idx1] = auxArray[idx2];
    auxArray[idx2] = temp;
}
